import './App.css';
import React, { useState, useEffect } from 'react';

const Alert = ({ message, duration = 2000, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, duration);

    return () => clearTimeout(timer);
  }, [duration, onClose]);

  return (
    <div className="alert">
      {message}
    </div>
  );
};

const images = import.meta.glob('./static/images/*.{jpg,png,svg}', { eager: true });

const copyImageToClipboard = async (imageUrl, setAlertMessage) => {
  try {
      // Fetch the image
      const response = await fetch(imageUrl);
      const blob = await response.blob();

      // Convert the blob to a Data URL
      const reader = new FileReader();
      reader.readAsDataURL(blob);

      reader.onloadend = async () => {
          const dataUrl = reader.result;


          setAlertMessage('Image started to download');
      };
  } catch (error) {
      console.error('Failed to copy image to clipboard:', error);
          setAlertMessage('Failed to copy image.');
  }
};

const ImageGallery = () => {
      const [alertMessage, setAlertMessage] = useState('');


  const handleAlertClose = () => {
    setAlertMessage('');
  };

    const imageElements = Object.keys(images).map((image, index) => {
        const imageUrl = image;
        return (
            <div key={index} className="card">
              <a download={`image${index + 1}`} href={`/src/${imageUrl}`}>
            <img src={`/src/${imageUrl}`} alt={`image${index + 1}`} onClick={() => copyImageToClipboard(`/src/${imageUrl}`, setAlertMessage)}
          style={{ cursor: 'pointer' }}/>
          </a>
            </div>
        );
    });

      return (
    <div className="showcase">
      {imageElements}
      {alertMessage && (
        <Alert
          message={alertMessage}
          duration={2000} // Duration in milliseconds
          onClose={handleAlertClose}
        />
      )}
    </div>
  );
};

function App() {
  return (
    <div className="App">
      <div className="header">
      <div className="name">
      <span>Collection</span>
      </div>
      </div>
      <div id="container" className="container">
      <ImageGallery />
      </div>
    </div>
  );
}

export default App;
